svg.script:
const titles = ["Data Scientist", "AI Builder", "Machine Learning Enthusiast", "Full Stack Learner"];
const typingEl = document.getElementById('typing');
let i = 0;
let charIndex = 0;
let isDeleting = false;

function typeEffect() {
  const current = titles[i];
  const display = current.substring(0, charIndex);
  typingEl.textContent = display;

  if (!isDeleting && charIndex < current.length) {
    charIndex++;
    setTimeout(typeEffect, 120);
  } else if (isDeleting && charIndex > 0) {
    charIndex--;
    setTimeout(typeEffect, 60);
  } else {
    if (!isDeleting) {
      isDeleting = true;
      setTimeout(typeEffect, 1000);
    } else {
      isDeleting = false;
      i = (i + 1) % titles.length;
      setTimeout(typeEffect, 300);
    }
  }
}

typeEffect();
